css:
.top-bar a.top-bar__countdown{
  background: #0e397b;
}
.info-bar {
    background: #0096db;
}
.header {
    background: white;
}
.header__category:hover {
    background: #004fa1;
    text-decoration: none;
    padding: 10px;
    color: white;
}
.header__nav a {
    border-radius: var(--border-radius-sm);
    display: inline-block;
    font-weight: var(--font-headings);
    padding: 0 var(--spacer-1);
    transition: background-color .15s;
    color: #024b9f;
}
#product-actions > div > div.product__buy > div > span > button{
      width: 100%;
    background: green;
    border: none;
    border-radius: 0px;
    text-transform: uppercase;
}
